/**
 *  @file    result.hpp
 *  @author  Alessandra Fais
 *  @date    14/07/2019
 *
 *  @brief Structure of a result tuple
 *
 *  This file defines the structure of the tuples generated by the splitter.
 *  The data type tuple_t must be default constructible, with a copy constructor
 *  and copy assignment operator, and it must provide and implement the setInfo() and
 *  getInfo() methods.
 */

#ifndef WORDCOUNT_RESULT_HPP
#define WORDCOUNT_RESULT_HPP

#include <windflow.hpp>

using namespace std;

struct result_t {
    string key;             // key word
    uint64_t id;            // id that indicates the current number of occurrences of the key word
    uint64_t ts;            // timestamp

    // default constructor
    result_t(): key(""), id(0), ts(0) {}

    // constructor
    result_t(string& _key, uint64_t _id, uint64_t _ts): key(_key), id(_id), ts(_ts) {}

    // getControlFields method
    tuple<string, uint64_t, uint64_t> getControlFields() const {
        return tuple<string, uint64_t, uint64_t>(key, id, ts);
    }

    // setControlFields method
    void setControlFields(string& _key, uint64_t _id, uint64_t _ts) {
        key = _key;
        id = _id;
        ts = _ts;
    }

    // destructor
    ~result_t() {}
};

#endif //WORDCOUNT_RESULT_HPP
